    function _getRequest(requestObject) {
        let canceler = $q.defer();
        requestObject.timeout = canceler.promise;

        let httpPromise = $http(requestObject).then(function(response) {
            return response.data;
        });

        httpPromise.cancel = function() {
            canceler.resolve();
        }

        return httpPromise;
    }

