let IZVOR_KALKULACIJE_UPB = 1;  // Osnovica
let IZVOR_KALKULACIJE_UPA = 2;  // Puna cijena

let PAYMENT_METHOD_TYPE_BANKNOTE = 1;
let PAYMENT_METHOD_TYPE_CARD = 2;
let PAYMENT_METHOD_TYPE_CHECK = 3;
